    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed

        String keyword = tempatsearch.getText();

        dao.PelangganDao dao = new dao.PelangganDao();

        // Panggil method SEARCH dari DAO
        java.util.List<model.Pelanggan> hasilPencarian = dao.searchByName(keyword);

        // --- Masukkan hasil ke JTable (Logika mirip tampilkanData) ---
        javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) tabelpelanggan.getModel();
        model.setRowCount(0); // Kosongkan tabel dulu

        if (hasilPencarian.isEmpty()) {
            javax.swing.JOptionPane.showMessageDialog(this, "Data tidak ditemukan!");
        } else {
            for (model.Pelanggan f : hasilPencarian) {
                Object[] baris = {
                    f.getIdPelanggan(),
                    f.getNamaPelanggan(),
                    f.getNoTelepon(),
                };
                model.addRow(baris);
            }
        }
    }//GEN-LAST:event_searchActionPerformed

