    private void updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateActionPerformed

        String idPelanggan = tempatid.getText();
        String namaPelanggan = tempatnama.getText();
        String noHp = tempatnohp.getText();

        // 2. Validasi (ID tidak boleh kosong karena itu kuncinya)
        if (idPelanggan.isEmpty()) {
            javax.swing.JOptionPane.showMessageDialog(this, "Pilih data yang akan diedit atau isi ID!");
            return;
        }

        try {

            model.Pelanggan pel = new model.Pelanggan();
            pel.setIdPelanggan(idPelanggan);
            pel.setNamaPelanggan(namaPelanggan);
            pel.setNoTelepon(noHp);

            dao.PelangganDao dao = new dao.PelangganDao();

            // 3. Panggil method UPDATE
            boolean sukses = dao.update(pel);

            if (sukses) {
                javax.swing.JOptionPane.showMessageDialog(this, "Data Berhasil Diupdate!");
                tampilkanData(); // Refresh tabel

                // Bersihkan form
                tempatid.setText("");
                tempatnama.setText("");
                tempatnohp.setText("");
            } else {
                javax.swing.JOptionPane.showMessageDialog(this, "Gagal Mengupdate Data.");
            }

        } catch (NumberFormatException e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Harga harus angka!");
        } catch (Exception e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Error: " + e.getMessage());
        }
    }//GEN-LAST:event_updateActionPerformed

    private void deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteActionPerformed
        String idPelanggan = tempatid.getText();

        // 1. Validasi ID
        if (idPelanggan.isEmpty()) {
            javax.swing.JOptionPane.showMessageDialog(this, "Pilih data yang akan dihapus atau isi ID!");
            return;
        }

        // 2. Konfirmasi User (Yes/No)
        int confirm = javax.swing.JOptionPane.showConfirmDialog(this,
            "Yakin ingin menghapus data ID: " + idPelanggan + "?",
            "Konfirmasi Hapus",
            javax.swing.JOptionPane.YES_NO_OPTION);

        if (confirm == javax.swing.JOptionPane.YES_OPTION) {
            dao.PelangganDao dao = new dao.PelangganDao();

            // 3. Panggil method DELETE
            boolean sukses = dao.delete(idPelanggan);

            if (sukses) {
                javax.swing.JOptionPane.showMessageDialog(this, "Data Berhasil Dihapus!");
                tampilkanData(); // Refresh tabel

                // Bersihkan form
                tempatid.setText("");
                tempatnama.setText("");
                tempatnohp.setText("");
            } else {
                javax.swing.JOptionPane.showMessageDialog(this, "Gagal Menghapus Data.");
            }
        }
    }//GEN-LAST:event_deleteActionPerformed

    private void clearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clearActionPerformed
        tempatid.setText("");
        tempatnama.setText("");
        tempatnohp.setText("");

        // Opsional: Kembalikan tabel ke posisi default (load semua data)
        tampilkanData();
        tempatid.requestFocus(); // Arahkan kursor kembali ke kolom ID
    }//GEN-LAST:event_clearActionPerformed

