    private void viewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_viewActionPerformed
        tampilkanData();
    }//GEN-LAST:event_viewActionPerformed
    
    private void tampilkanData() {
    // 1. Siapkan Model Tabel
    // Pastikan nama variabel tabelmu sesuai (misal: jTable1 atau tblData)
    javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) tabelpelanggan.getModel();
    
    // 2. Kosongkan tabel dulu agar tidak dobel saat direfresh
    model.setRowCount(0);

    // 3. Ambil data dari Database pakai DAO
    dao.PelangganDao dao = new dao.PelangganDao();
    java.util.List<model.Pelanggan> listPelanggan = dao.getAll();

    // 4. Masukkan data ke Tabel
    for (model.Pelanggan f : listPelanggan) {
        Object[] baris = {
            f.getIdPelanggan(),
            f.getNamaPelanggan(),
            f.getNoTelepon(),
            // Tambahkan nama vendor jika ada di model
        };
        model.addRow(baris);
    }
}
    
